        // ReSharper disable once NotAccessedField.Local
        private readonly SandboxSession _session;

        private readonly SshPty _pty;

        private bool _redraw = true;

        private readonly Random _rnd = new((int)(DateTime.UtcNow.Ticks & int.MaxValue));

        public bool IsRunning { get; private set; }

        public Window(SandboxSession sandboxSession, SshPty pty)
        {
            _session = sandboxSession;

            IsRunning = true;

            _pty = pty;
            _pty.Disconnect += OnDisconnected;
            _pty.Data += OnData;
            _pty.Resize += OnResize;

            _pty.Reset();
            _pty.LinewrapDisable();
            _pty.CursorHide();

            Task.Run(Run);
        }

        public void Run()
        {
            while (IsRunning)
            {
                Draw();

                Thread.Sleep(1000);
            }
        }

        public void Stop()
        {
            IsRunning = false;
        }

        private void RedrawScreen()
        {
            _pty.Clear();
            _pty.WriteR('═', _pty.WidthChars);
            _pty.Send($" 🖥:{_pty.WidthChars}x{_pty.HeightChars} 🕒:{DateTime.UtcNow:R}");
            _pty.Cursor(0, 3);
            _pty.WriteR('═', _pty.WidthChars);
        }

        private void OnDisconnected(string reason)
        {
            Stop();
        }

        // ReSharper disable once MemberCanBeMadeStatic.Local
        private void OnData(string data)
        {

        }

        private void OnResize(uint wChar, uint hChar, uint wPx, uint hPx)
        {
            _redraw = true;
        }

        private void Draw()
        {
            if (_redraw)
            {
                RedrawScreen();

                _redraw = false;
            }

            for (var idx = 0; idx <= 5; idx++)
            {
                _pty.Cursor(_rnd.Next(1, (int)_pty.WidthChars), _rnd.Next(4, (int)_pty.HeightChars - 1));
                _pty.Send(_emojis[_rnd.Next(_emojis.Count)]); // "┼"); // _emojis[_rnd.Next(_emojis.Count)]); // _rnd.Next(2) == 0 ? "┼" : "╬"); // _emojis[_rnd.Next(_emojis.Count)]);
            }

            _pty.Cursor(0, 2);
            _pty.Send($" 🖥:{_pty.WidthChars}x{_pty.HeightChars} 🕒:{DateTime.UtcNow:R}     ");
        }
